var classes = require("./classes.js");
var colors = require("colors");
var program = require("commander");
var version = "1.0.3";

program
    .version(version)
    .option("-w, --watch", "Holds this CLI open and executes the analyzer whenever some file changes within the given base directory")
    .option("-d, --directory <basedir>", "Specifies the base directory from which the analyzer must start the search of JavaScript dependencies")
    .option("-i, --includes <includes>", "A semicolon-separated list of other moduleinfo.js to include to current analysis. They can be local paths or HTTP/HTTPS absolute URIs.")
    .option("-e, --excludes <excludes>", "A semicolon-separated list of files or directory paths to exclude from the analysis.")
    .option("-o, --baseDirectoryOverrides <baseDirectoryOverrides>", "A semicolon-separated list of key-value pairs where key is the base directory to override and the value is the expected result expression")
    .option("-q, --quiet", "Quiet mode: no console output")
    .option("-n, --nologo", "Hides jOOPL logo")
    .parse(process.hasOwnProperty("argv") ? process.argv : []);

if(!program.hasOwnProperty("nologo")) {
    console.log("\n*********************************\n");
    console.log("\tjOOPL Analyzer " + version);
    console.log("\thttp://mfidemraizer.github.io/joopl/");
    console.log();
    console.log("\tThis program is distributed under \n\tApache License 2.0\n\t(http://www.apache.org/licenses/LICENSE-2.0.html)\n");
    console.log("\t(c) Matias Fidemraizer\n\t(https://www.linkedin.com/in/mfidemraizer)\n");
    console.log("*********************************\n");
}

if(!program.hasOwnProperty("quiet")) {
    program.quiet = false;
}

if (!program.hasOwnProperty("directory") || program.directory == "") {
    classes.Logger.default.logAlways("No base directory was given, using current directory...".yellow);

    program.directory = ".";
} else {
    classes.Logger.default.logAlways("Using '%s' as base directory...", program.directory);
}

if (program.hasOwnProperty("includes")) {
    program.includes = program.includes.split(";");
} else {
    program.includes = [];
}

if (program.hasOwnProperty("excludes")) {
    program.excludes = program.excludes.split(";");
} else {
    program.excludes = [];
}

if (program.hasOwnProperty("baseDirectoryOverrides")) {
    var result = program.baseDirectoryOverrides.split(";");
    result = result.map(function(pairText) {
        var keyValuePair = pairText.split("=");

        return { "startsWith": keyValuePair[0], "replaceWith": keyValuePair[1] };
    });

    program.baseDirectoryOverrides = result;
} else {
    program.baseDirectoryOverrides = [];
}

var analyzer = new classes.DependencyAnalyzer();
analyzer.start({ quiet: program.quiet, baseDirectory: program.directory, includes: program.includes, fileExcludes: program.excludes, baseDirectoryOverrides: program.baseDirectoryOverrides });