// Fake
$import = {
	_dependencies: null,

	mapMany: function(dependencies) {
		this._dependencies = dependencies;
	}
};

require("./test-hierarchy/moduleinfo.js");

exports.joopl_analyzer_ns4_SomeClass_hasExcpectedDependencies = function(test) {
	test.ok($import._dependencies.hasOwnProperty("joopl.analyzer.ns4.SomeClass"), "joopl.analyzer.ns4.SomeClass class has been detected");

	var dependencies = $import._dependencies["joopl.analyzer.ns4.SomeClass"];

	test.equal(6, dependencies.length, "6 files should be the count of detected file dependencies");

	test.ok(dependencies.indexOf("./ClassWithPropertiesAndInstances.js") == 0, "ClassWithPropertiesAndInstances class found and it has been detected in the right order");
	test.ok(dependencies.indexOf("./ns1/A.js") == 1, "ns1.A class found and it has been detected in the right order");
	test.ok(dependencies.indexOf("./ns2/A.js") == 2, "ns2.A class found and it has been detected in the right order");
	test.ok(dependencies.indexOf("./ns2/B.js") == 3, "ns2.B class found and it has been detected in the right order");
	test.ok(dependencies.indexOf("./ns3/C.js") == 4, "ns1.B class found and it has been detected in the right order");
	test.ok(dependencies.indexOf("./ns1/B.js") == 5, "ns3.C class found and it has been detected in the right order");

	test.done();
};

