$import = {
	_dependencies: null,

	mapMany: function(dependencies) {
		this._dependencies = dependencies;
	}
};

exports.SomeClass_HasExpectedDependencies = function(test) {
	require("./test-hierarchy/moduleinfo.js");

	test.ok($import._dependencies.hasOwnProperty("joopl.analyzer.SomeClass"), "joopl.analyzer.SomeClass class has been detected");

	var dependencies = $import._dependencies["joopl.analyzer.SomeClass"];

	test.equal(5, dependencies.length, "5 files should be the count of detected file dependencies");

	test.ok(dependencies.indexOf("./ns2/A.js") > -1, "ns2.A class found");
	test.ok(dependencies.indexOf("./ns2/B.js") > -1, "ns2.B class found");
	test.ok(dependencies.indexOf("./SomeClass.js") > -1, "SomeClass class found");
	test.ok(dependencies.indexOf("./ns1/A.js") > -1, "ns1.A class found");
	test.ok(dependencies.indexOf("./ns1/B.js") > -1, "ns1.B class found");

	test.done();
}